var should = require('should');
var csv2json = require('../');
var fs = require('fs');

describe('csv to json', function() {

	it('should convert csv to json', function() {
		csv2json({
			input: './sample/sample-csv.csv',
			output: null
		}, function(err, result) {
			should.not.exist(err)
			result.should.be.an.instanceOf(Object)
		})
	})

	it('should convert csv to json file', function() {
		csv2json({
			input: './sample/sample-csv.csv',
			output: './sample/test.json'
		}, function(err, result) {
			should.not.exist(err)
			result.should.be.an.instanceOf(Object)
		})

	})

	it('should read file in test.json', function() {
		var exist = fs.existsSync('./sample/test.json')
		exist.should.be.true;
	})

})